// Parse GPT response to structured format
const response = $input.first().json.message?.content || $input.first().json.text || '';

let parsed;
try {
  // Try to parse as JSON
  const jsonMatch = response.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    parsed = JSON.parse(jsonMatch[0]);
  } else {
    parsed = {
      approval_status: 'NEEDS_REVISION',
      summary: response.substring(0, 200),
      strengths: [],
      concerns: ['Could not parse structured response'],
      suggestions: [],
      priority: 'MEDIUM'
    };
  }
} catch (e) {
  parsed = {
    approval_status: 'NEEDS_REVISION',
    summary: 'Response parsing failed',
    strengths: [],
    concerns: [e.message],
    suggestions: [],
    priority: 'HIGH',
    raw_response: response
  };
}

return {
  ...parsed,
  reviewed_at: new Date().toISOString(),
  source: 'n8n-gpt-workflow'
};
